    /**
     * Transforms a ResultSet iterating on the client table on a ClientBean bean according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @param pos start index of the resultset
     * @return bean resulting ClientBean bean
     * @throws DAOException
     */
    //30
    public ClientBean decodeRow(ResultSet rs, int[] fieldList, Integer pos) throws DAOException
    {
        ClientBean bean = this.createClientBean();
        try
        {
		java.util.Date date = null;		
            for(int i = 0; i < fieldList.length; i++)
            {
                switch(fieldList[i])
                {
                    case ID_CREATION_TIMESTAMP:
						if ((date = rs.getTimestamp(pos)) != null)
			{
				bean.setCreationTimestamp(date.getTime());
			}
	 	   				pos++;
                        break;
                    case ID_CONTACT_NUMBER:
						bean.setContactNumber(rs.getString(pos));
	    					pos++;
                        break;
                    case ID_EMAIL:
						bean.setEmail(rs.getString(pos));
	    					pos++;
                        break;
                    case ID_ADDRESS:
						bean.setAddress(rs.getString(pos));
	    					pos++;
                        break;
                    case ID_CLIENT_NAME:
						bean.setClientName(rs.getString(pos));
	    					pos++;
                        break;
                    case ID_CLIENT_ID:
						bean.setClientId(Manager.getInteger(rs, pos));
	    					pos++;
                        break;
                    default:
                        throw new DAOException("Unknown field id " + fieldList[i]);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified(false);

        return bean;
    }

    /**
     * Transforms a ResultSet iterating on the client on a ClientBean bean using the names of the columns
     *
     * @param rs the ResultSet to be transformed
     * @return bean resulting ClientBean bean
     * @throws DAOException
     */
    //31
    public ClientBean metaDataDecodeRow(ResultSet rs) throws DAOException
    {
		java.util.Date date = null;
        ClientBean bean = this.createClientBean();
        try
        {
						if ((date = rs.getTimestamp("client.creation_timestamp")) != null)
			{
				bean.setCreationTimestamp(date.getTime());
			}
	 	   							bean.setContactNumber(rs.getString("client.contact_number"));
	    								bean.setEmail(rs.getString("client.email"));
	    								bean.setAddress(rs.getString("client.address"));
	    								bean.setClientName(rs.getString("client.client_name"));
	    								bean.setClientId(Manager.getInteger(rs, "client.client_id"));
	    		        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }

        bean.isNew(false);
        bean.resetIsModified(false);

        return bean;
    }

