    //


    /**
     * fills the given StringBuilder with the sql where clausis constructed using the bean and the search type
     * @param sqlWhere the StringBuilder that will be filled
     * @param bean the bean to use for creating the where clausis
     * @param searchType exact ?  like ? starting like ?
     * @return the number of clausis returned
     */
    protected int fillWhere(StringBuilder sqlWhere, FactBean bean, int searchType)
    {
        if (bean == null) {
            return 0;
        }
        int _dirtyCount = 0;
        String sqlEqualsOperation = "=";
        if (searchType != SEARCH_EXACT) {
            sqlEqualsOperation = " like ";
        }
        try
        {
            if (bean.isCreationTimestampModified()) {
                _dirtyCount ++;
                if (bean.getCreationTimestamp() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("creation_timestamp IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("creation_timestamp = ?");
                }
            }
            if (bean.isTextModified()) {
                _dirtyCount ++;
                if (bean.getText() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("text IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("text ").append(sqlEqualsOperation).append("?");
                }
            }
            if (bean.isFactIdModified()) {
                _dirtyCount ++;
                if (bean.getFactId() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("fact_id IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("fact_id = ?");
                }
            }
        }
        finally
        {
            sqlEqualsOperation = null;
        }
        return _dirtyCount;
    }

    /**
     * fill the given prepared statement with the bean values and a search type
     * @param ps the PreparedStatement that will be filled
     * @param bean the bean to use for creating the where clausis
     * @param searchType exact ?  like ? starting like ?
     * @return the number of clausis returned
     * @throws DAOException
     */
    protected int fillPreparedStatement(PreparedStatement ps, FactBean bean, int searchType) throws DAOException
    {
        if (bean == null) {
            return 0;
        }
        int _dirtyCount = 0;
        try
        {
            if (bean.isCreationTimestampModified()) {
                // System.out.println("Setting for " + _dirtyCount + " [" + bean.getCreationTimestamp() + "]");
                if (bean.getCreationTimestamp() == null) { ps.setNull(++_dirtyCount, Types.TIMESTAMP); } else { ps.setTimestamp(++_dirtyCount, new java.sql.Timestamp(bean.getCreationTimestamp().getTime())); }
            }
            if (bean.isTextModified()) {
                switch (searchType) {
                    case SEARCH_EXACT:
                        // System.out.println("Setting for " + _dirtyCount + " [" + bean.getText() + "]");
                        if (bean.getText() == null) { ps.setNull(++_dirtyCount, Types.VARCHAR); } else { ps.setString(++_dirtyCount, bean.getText()); }
                        break;
                    case SEARCH_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [%" + bean.getText() + "%]");
                        ps.setString(++_dirtyCount, "%" + bean.getText() + "%");
                        break;
                    case SEARCH_STARTING_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [" + bean.getText() + "%]");
                        ps.setString(++_dirtyCount, "%" + bean.getText());
                        break;
                    case SEARCH_ENDING_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [%" + bean.getText() + "]");
                        if (bean.getText() + "%" == null) { ps.setNull(++_dirtyCount, Types.VARCHAR); } else { ps.setString(++_dirtyCount, bean.getText() + "%"); }
                        break;
                    default:
                        throw new DAOException("Unknown search type " + searchType);
                }
            }
            if (bean.isFactIdModified()) {
                // System.out.println("Setting for " + _dirtyCount + " [" + bean.getFactId() + "]");
                if (bean.getFactId() == null) { ps.setNull(++_dirtyCount, Types.INTEGER); } else { Manager.setInteger(ps, ++_dirtyCount, bean.getFactId()); }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        return _dirtyCount;
    }


