    /**
     * fills the given StringBuilder with the sql where clausis constructed using the bean and the search type
     * @param sqlWhere the StringBuilder that will be filled
     * @param bean the bean to use for creating the where clausis
     * @param searchType exact ?  like ? starting like ?
     * @return the number of clausis returned
     */
    protected int fillWhere(StringBuilder sqlWhere, MessageAlertsBean bean, int searchType)
    {
        if (bean == null) {
            return 0;
        }
        int _dirtyCount = 0;
        String sqlEqualsOperation = "=";
        if (searchType != SEARCH_EXACT) {
            sqlEqualsOperation = " like ";
        }
        try
        {
            if (bean.isScheduledTimeModified()) {
                _dirtyCount ++;
                if (bean.getScheduledTime() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("scheduled_time IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("scheduled_time = ?");
                }
            }
            if (bean.isTextModified()) {
                _dirtyCount ++;
                if (bean.getText() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("text IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("text ").append(sqlEqualsOperation).append("?");
                }
            }
            if (bean.isAlertIdModified()) {
                _dirtyCount ++;
                if (bean.getAlertId() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("alert_id IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("alert_id = ?");
                }
            }
        }
        finally
        {
            sqlEqualsOperation = null;
        }
        return _dirtyCount;
    }

