    /**
     * fills the given StringBuilder with the sql where clausis constructed using the bean and the search type
     * @param sqlWhere the StringBuilder that will be filled
     * @param bean the bean to use for creating the where clausis
     * @param searchType exact ?  like ? starting like ?
     * @return the number of clausis returned
     */
    protected int fillWhere(StringBuilder sqlWhere, PromoBean bean, int searchType)
    {
        if (bean == null) {
            return 0;
        }
        int _dirtyCount = 0;
        String sqlEqualsOperation = "=";
        if (searchType != SEARCH_EXACT) {
            sqlEqualsOperation = " like ";
        }
        try
        {
            if (bean.isCreationTimestampModified()) {
                _dirtyCount ++;
                if (bean.getCreationTimestamp() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("creation_timestamp IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("creation_timestamp = ?");
                }
            }
            if (bean.isDetailModified()) {
                _dirtyCount ++;
                if (bean.getDetail() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("detail IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("detail ").append(sqlEqualsOperation).append("?");
                }
            }
            if (bean.isCodeModified()) {
                _dirtyCount ++;
                if (bean.getCode() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("code IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("code ").append(sqlEqualsOperation).append("?");
                }
            }
            if (bean.isPromoIdModified()) {
                _dirtyCount ++;
                if (bean.getPromoId() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("promo_id IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("promo_id = ?");
                }
            }
        }
        finally
        {
            sqlEqualsOperation = null;
        }
        return _dirtyCount;
    }

    /**
     * fill the given prepared statement with the bean values and a search type
     * @param ps the PreparedStatement that will be filled
     * @param bean the bean to use for creating the where clausis
     * @param searchType exact ?  like ? starting like ?
     * @return the number of clausis returned
     * @throws DAOException
     */
    protected int fillPreparedStatement(PreparedStatement ps, PromoBean bean, int searchType) throws DAOException
    {
        if (bean == null) {
            return 0;
        }
        int _dirtyCount = 0;
        try
        {
            if (bean.isCreationTimestampModified()) {
                // System.out.println("Setting for " + _dirtyCount + " [" + bean.getCreationTimestamp() + "]");
                if (bean.getCreationTimestamp() == null) { ps.setNull(++_dirtyCount, Types.TIMESTAMP); } else { ps.setTimestamp(++_dirtyCount, new java.sql.Timestamp(bean.getCreationTimestamp().getTime())); }
            }
            if (bean.isDetailModified()) {
                switch (searchType) {
                    case SEARCH_EXACT:
                        // System.out.println("Setting for " + _dirtyCount + " [" + bean.getDetail() + "]");
                        if (bean.getDetail() == null) { ps.setNull(++_dirtyCount, Types.VARCHAR); } else { ps.setString(++_dirtyCount, bean.getDetail()); }
                        break;
                    case SEARCH_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [%" + bean.getDetail() + "%]");
                        ps.setString(++_dirtyCount, "%" + bean.getDetail() + "%");
                        break;
                    case SEARCH_STARTING_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [" + bean.getDetail() + "%]");
                        ps.setString(++_dirtyCount, "%" + bean.getDetail());
                        break;
                    case SEARCH_ENDING_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [%" + bean.getDetail() + "]");
                        if (bean.getDetail() + "%" == null) { ps.setNull(++_dirtyCount, Types.VARCHAR); } else { ps.setString(++_dirtyCount, bean.getDetail() + "%"); }
                        break;
                    default:
                        throw new DAOException("Unknown search type " + searchType);
                }
            }
            if (bean.isCodeModified()) {
                switch (searchType) {
                    case SEARCH_EXACT:
                        // System.out.println("Setting for " + _dirtyCount + " [" + bean.getCode() + "]");
                        if (bean.getCode() == null) { ps.setNull(++_dirtyCount, Types.VARCHAR); } else { ps.setString(++_dirtyCount, bean.getCode()); }
                        break;
                    case SEARCH_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [%" + bean.getCode() + "%]");
                        ps.setString(++_dirtyCount, "%" + bean.getCode() + "%");
                        break;
                    case SEARCH_STARTING_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [" + bean.getCode() + "%]");
                        ps.setString(++_dirtyCount, "%" + bean.getCode());
                        break;
                    case SEARCH_ENDING_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [%" + bean.getCode() + "]");
                        if (bean.getCode() + "%" == null) { ps.setNull(++_dirtyCount, Types.VARCHAR); } else { ps.setString(++_dirtyCount, bean.getCode() + "%"); }
                        break;
                    default:
                        throw new DAOException("Unknown search type " + searchType);
                }
            }
            if (bean.isPromoIdModified()) {
                // System.out.println("Setting for " + _dirtyCount + " [" + bean.getPromoId() + "]");
                if (bean.getPromoId() == null) { ps.setNull(++_dirtyCount, Types.INTEGER); } else { Manager.setInteger(ps, ++_dirtyCount, bean.getPromoId()); }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        return _dirtyCount;
    }


