    /**
     * Transforms a ResultSet iterating on the subscriber table on a SubscriberBean bean according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @param pos start index of the resultset
     * @return bean resulting SubscriberBean bean
     * @throws DAOException
     */
    //30
    public SubscriberBean decodeRow(ResultSet rs, int[] fieldList, Integer pos) throws DAOException
    {
        SubscriberBean bean = this.createSubscriberBean();
        try
        {
		java.util.Date date = null;		
            for(int i = 0; i < fieldList.length; i++)
            {
                switch(fieldList[i])
                {
                    case ID_CREATION_TIMESTAMP:
						if ((date = rs.getTimestamp(pos)) != null)
			{
				bean.setCreationTimestamp(date.getTime());
			}
	 	   				pos++;
                        break;
                    case ID_DEVICE_ID:
						bean.setDeviceId(rs.getString(pos));
	    					pos++;
                        break;
                    case ID_FIREBASE_TOKEN_ID:
						bean.setFirebaseTokenId(rs.getString(pos));
	    					pos++;
                        break;
                    case ID_NAME:
						bean.setName(rs.getString(pos));
	    					pos++;
                        break;
                    case ID_MSISDN:
						bean.setMsisdn(rs.getString(pos));
	    					pos++;
                        break;
                    case ID_SUBSCRIBER_ID:
						bean.setSubscriberId(Manager.getInteger(rs, pos));
	    					pos++;
                        break;
                    default:
                        throw new DAOException("Unknown field id " + fieldList[i]);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified(false);

        return bean;
    }

